import React from 'react'
import './index.css';

const cardWidth = 70;
const cardHeight = 94;

const values = ['2', '3', '4', '5', '6', '7', '8', '9', 'T', 'J', 'K', 'Q', 'A']
const suits = ['H', 'C', 'D', 'S'];

function Card(props) {
    var xOffset = 0;
    var yOffset = 0;

    if ('value' in props && props.value.length === 2) {
        xOffset = values.indexOf(props.value[0], 0) + 1;
        yOffset = suits.indexOf(props.value[1], 0);
    }

    return (
        <div
         className='Card'
         style={{'background-image': 'url(https://img.itch.zone/aW1hZ2UvNzE1Mzg3LzM5NjQ5MzEucG5n/original/iwG3hK.png)',
                 width: cardWidth + 'px',
                 height: cardHeight + 'px',
                 overflow: 'hidden',
                 'background-position': -xOffset * cardWidth + 'px ' + -yOffset * cardHeight + 'px'
               }}
        />
    );
}

function dealDealer() {
    var out = [];
    out.push(<td><Card value={values[Math.floor(Math.random() * values.length)] + suits[Math.floor(Math.random() * suits.length)] + 'H'} /></td>);
    out.push(<td><Card value={values[Math.floor(Math.random() * values.length)] + suits[Math.floor(Math.random() * suits.length)]} /></td>);
    return (out);
}

function dealHand() {
    var out = [];
    out.push(<td><Card value={values[Math.floor(Math.random() * values.length)] + suits[Math.floor(Math.random() * suits.length)]} /></td>);
    out.push(<td><Card value={values[Math.floor(Math.random() * values.length)] + suits[Math.floor(Math.random() * suits.length)]} /></td>);
    out.push(<td><Card value={values[Math.floor(Math.random() * values.length)] + suits[Math.floor(Math.random() * suits.length)]} /></td>);
    out.push(<td><Card value={values[Math.floor(Math.random() * values.length)] + suits[Math.floor(Math.random() * suits.length)]} /></td>);
    out.push(<td><Card value={values[Math.floor(Math.random() * values.length)] + suits[Math.floor(Math.random() * suits.length)]} /></td>);
    out.push(<td><Card value={values[Math.floor(Math.random() * values.length)] + suits[Math.floor(Math.random() * suits.length)]} /></td>);
    out.push(<td><Card value={values[Math.floor(Math.random() * values.length)] + suits[Math.floor(Math.random() * suits.length)]} /></td>);
    return (out);
}

class Hit extends React.Component {
    render() {
        return (
            <div>
             <b>Hit</b>
             <Card />
            </div>
        );
    }
}

class Stand extends React.Component {
    render() {
        return (
            <div>
             <b>Stand</b>
             <Card />
            </div>
        );
    }
}

class Dealer extends React.Component {
    render() {
        return (
            <div>
             <table>{dealDealer()}</table>
             <b>Dealer Score:</b>
            </div>
        );
    }
}

class Hand extends React.Component {
    render() {
        return (
            <div>
             <b>Your Score:</b>
             <table>{dealHand()}</table>
            </div>
        );
    }
}

class Table extends React.Component {
    render() {
        return (
            <div>
             <div className='Hand'>
              <Hand />
             </div>
             <div className='Dealer'>
              <Dealer />
             </div>
             <div className='Hit'>
              <Hit />
             </div>
             <div className='Stand'>
              <Stand />
             </div>
            </div>
        );
    }
}

class Game extends React.Component {
    render() {
        return (
            <div className='Table'>
             <Table />
            </div>
        );
    }
}
export default Game;
