import React from 'react'
import './index.css';

const cardWidth = 70;
const cardHeight = 94;

const values = ['2', '3', '4', '5', '6', '7', '8', '9', 'T', 'J', 'K', 'Q', 'A']
const suits = ['H', 'C', 'D', 'S'];

function Card(props) {
    var xOffset = 0;
    var yOffset = 0;

    if ('value' in props && props.value.length === 2) {
        xOffset = values.indexOf(props.value[0], 0) + 1;
        yOffset = suits.indexOf(props.value[1], 0);
    }

    return (
        <div
         className='Card'
         style={{backgroundImage: 'url(https://img.itch.zone/aW1hZ2UvNzE1Mzg3LzM5NjQ5MzEucG5n/original/iwG3hK.png)',
                 width: cardWidth + 'px',
                 height: cardHeight + 'px',
                 overflow: 'hidden',
                 backgroundPosition: -xOffset * cardWidth + 'px ' + -yOffset * cardHeight + 'px'
               }}
        />
    );
}

function dealHand(arr, player) {
    var out = [];
    for (var i = 0; i < arr.length; i++) {
        out.push(<Card key={(player ? 'Player' : 'Dealer') + i} value={arr[i]} />);
    }
    return (out);
}

function Hit(props) {
    return (
        <div onClick={props.onClick}>
         <b>Hit</b><br />
         <Card />
        </div>
    );
}

function Stand(props) {
    return (
        <div onClick={props.onClick}>
         <b>Stand</b><br />
         <Card />
        </div>
    );
}

class Table extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            deck: [...Array(52).keys()],
            dealerHand: [],
            playerHand: [],
        };

        this.shuffleDeck();

        this.state.playerHand.push(this.getCard(this.state.deck.pop()));
        this.state.playerHand.push(this.getCard(this.state.deck.pop()));

        this.state.dealerHand.push(this.getCard(this.state.deck.pop()) + 'H');
        this.state.dealerHand.push(this.getCard(this.state.deck.pop()));
        if (this.calculateScore(this.state.dealerHand, true) === 'Blackjack!') {
            this.state.dealerHand[0] = this.state.dealerHand[0].slice(0,-1);
        }
    }

    shuffleDeck() {
        var start = this.state.deck;
        var shuffled = [];

        while (start.length > 0) {
            var index = Math.floor(Math.random() * start.length);
            var next = start.splice(index, 1);
            shuffled.push(next[0]);
        }

        this.state.deck = shuffled;
    }

    calculateScore(arr, addHidden) {
        var out = '';
        var score = 0;
        for (var i = 0; i < arr.length; i++) {
            // Don't include hidden value
            if (!addHidden && arr[i].length > 2) {
                out += '>';
                continue;
            }
            var val = arr[i][0];
            if (!isNaN(val)) {
                score += parseInt(val);
            } 
            else if (val !== 'A') {
                score += 10;
            }
            else if (score + 11 <= 21) {
                score += 11;
            }
            else {
                score += 1;
            }

        }
        if (score === 21) {
            out = 'Blackjack!';
        }
        else if (score > 21) {
            out = 'Bust!'
        }
        else {
            out += score.toString();
        }

        return out;
    }

    getCard(cardVal) {
        var suit = Math.floor(cardVal / 13);
        var value = cardVal % 13;
        var out = values[value] + suits[suit]
        return out;
    }

    handleHit() {
        var hand = this.state.playerHand;
        if (this.calculateScore(this.state.dealerHand, true) === 'Blackjack!' || this.calculateScore(hand, false) === 'Bust!' || this.calculateScore(hand, false) === 'Blackjack!') {
            return;
        }
        var deck = this.state.deck;
        hand.push(this.getCard(deck.pop()));
        this.setState({
            deck: deck,
            playerHand: hand,
            dealerHand: this.state.dealerHand,
        });

        /* Handle Bust */
    }

    handleStand() {
        console.log('Stand');
    }

    render() {
        return (
            <div>
             <div className='Hand'>
              <b>You: {this.calculateScore(this.state.playerHand, false)}</b><br />
              <div>
               {dealHand(this.state.playerHand, true)}
              </div>
             </div>
             <div className='Dealer'>
              <div>
               {dealHand(this.state.dealerHand, false)}
              </div>
              <b>Dealer: {this.calculateScore(this.state.dealerHand, false)}</b><br />
             </div>
             <div className='Hit'>
              <Hit onClick={() => this.handleHit()} />
             </div>
             <div className='Stand'>
              <Stand  onClick={() => this.handleStand()} />
             </div>
            </div>
        );
    }
}

class Game extends React.Component {
    render() {
        return (
            <div className='Table'>
             <Table />
            </div>
        );
    }
}
export default Game;
