import React from 'react'

const cardWidth = 70;
const cardHeight = 94;

const suits = [''];

function Card(props) {
    var xOffset = props.x;
    var yOffset = props.y;

    return (
        <div
         class='Card'
         style={{'background-image': 'url(https://img.itch.zone/aW1hZ2UvNzE1Mzg3LzM5NjQ5MzEucG5n/original/iwG3hK.png)',
                 width: cardWidth + 'px',
                 height: cardHeight + 'px',
                 overflow: 'hidden',
                 'background-position': -xOffset * cardWidth + 'px ' + -yOffset * cardHeight + 'px'
               }}
        />
    );
}

class Deck extends React.Component {
    render() {
        return (
            <Card x={0} y={0} />
        );

    }
}

class Game extends React.Component {
    render() {
        return (
            <div class='Game'>
             <Card x={1} y={0} />
             <Card x={1} y={1} />
             <Card x={1} y={2} />
             <Card x={1} y={3} />
             <Deck />
            </div>
        );
    }
}
export default Game;
