import React from 'react'
import './index.css';

const cardWidth = 70;
const cardHeight = 94;

const values = ['2', '3', '4', '5', '6', '7', '8', '9', 'T', 'J', 'K', 'Q', 'A']
const suits = ['H', 'C', 'D', 'S'];

function Card(props) {
    var xOffset = 0;
    var yOffset = 0;

    if ('value' in props) {
        xOffset = values.indexOf(props.value[0], 0) + 1;
        yOffset = suits.indexOf(props.value[1], 0);
    }

    return (
        <div
         class='Card'
         style={{'background-image': 'url(https://img.itch.zone/aW1hZ2UvNzE1Mzg3LzM5NjQ5MzEucG5n/original/iwG3hK.png)',
                 width: cardWidth + 'px',
                 height: cardHeight + 'px',
                 overflow: 'hidden',
                 'background-position': -xOffset * cardWidth + 'px ' + -yOffset * cardHeight + 'px'
               }}
        />
    );
}

function generateHand() {
    var out = [];
    for (var i = 0; i < 5; i++) {
        out.push(<td><Card value={values[Math.floor(Math.random() * values.length)] + suits[Math.floor(Math.random() * suits.length)]} /></td>);
    }
    return (out);
}

class Deck extends React.Component {
    render() {
        return (
            <Card />
        );

    }
}

class Game extends React.Component {
    render() {
        return (
            <div class='Game' style={{width: '80%', height: '100%', 'align-objects': 'center'}}>
             <span class='Hand'>
              <table>
               <Card value={values[Math.floor(Math.random() * values.length)] + suits[Math.floor(Math.random() * suits.length)]} /><td />
               {generateHand()}
              </table>
             </span>
             <span class='Deck'>
              <Deck />
             </span>
            </div>
        );
    }
}
export default Game;
