import React from 'react'
import './index.css';

const cardWidth = 70;
const cardHeight = 94;

const values = ['2', '3', '4', '5', '6', '7', '8', '9', 'T', 'J', 'K', 'Q', 'A']
const suits = ['H', 'C', 'D', 'S'];

function Card(props) {
    var xOffset = 0;
    var yOffset = 0;

    if ('value' in props && props.value.length === 2) {
        xOffset = values.indexOf(props.value[0], 0) + 1;
        yOffset = suits.indexOf(props.value[1], 0);
    }

    return (
        <div
         className='Card'
         style={{backgroundImage: 'url(https://img.itch.zone/aW1hZ2UvNzE1Mzg3LzM5NjQ5MzEucG5n/original/iwG3hK.png)',
                 width: cardWidth + 'px',
                 height: cardHeight + 'px',
                 overflow: 'hidden',
                 backgroundPosition: -xOffset * cardWidth + 'px ' + -yOffset * cardHeight + 'px'
               }}
        />
    );
}

function dealDealer() {
    var out = [];
    out.push(<Card key='Dealer1' value={values[Math.floor(Math.random() * values.length)] + suits[Math.floor(Math.random() * suits.length)] + 'H'} />);
    out.push(<Card key='Dealer2' value={values[Math.floor(Math.random() * values.length)] + suits[Math.floor(Math.random() * suits.length)]} />);
    return (out);
}

function dealHand() {
    var out = [];
    out.push(<Card key='Player1' value={values[Math.floor(Math.random() * values.length)] + suits[Math.floor(Math.random() * suits.length)]} />);
    out.push(<Card key='Player2' value={values[Math.floor(Math.random() * values.length)] + suits[Math.floor(Math.random() * suits.length)]} />);
    return (out);
}

class Hit extends React.Component {
    render() {
        return (
            <div>
             <b>Hit</b><br />
             <Card />
            </div>
        );
    }
}

class Stand extends React.Component {
    render() {
        return (
            <div>
             <b>Stand</b><br />
             <Card />
            </div>
        );
    }
}

class Dealer extends React.Component {
    render() {
        return (
            <div>
             {dealDealer()}
            </div>
        );
    }
}

class Hand extends React.Component {
    render() {
        return (
            <div>
             {dealHand()}
            </div>
        );
    }
}

class Table extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            dealerScore: 0,
            playerScore: 0,
        };
    }

    handleHit() {
        console.log('Hit!');
    }

    handleStand() {
        console.log('Stand');
    }

    render() {
        return (
            <div>
             <div className='Hand'>
              <b>You: {this.state.playerScore}</b><br />
              <Hand />
             </div>
             <div className='Dealer'>
              <Dealer />
              <b>Dealer: {this.state.dealerScore}</b><br />
             </div>
             <div className='Hit'>
              <Hit onClick={() => this.handleHit()} />
             </div>
             <div className='Stand'>
              <Stand  onClick={() => this.handleStand()} />
             </div>
            </div>
        );
    }
}

class Game extends React.Component {
    render() {
        return (
            <div className='Table'>
             <Table />
            </div>
        );
    }
}
export default Game;
